import React, { Component } from "react";
import jwt_decode from "jwt-decode";
import "./home.css";
import { Link } from "react-router-dom";

class Home extends Component {
  state = { name: null, loggedIn: false, website: '' };
  handleWebsiteChange = (e) => {
    this.setState({website: e.target.value})
  }
  addWebsite = async () => {
    const response = await fetch("http://localhost:8080/api/add_website", {
      method: "POST",
      headers: {
        'x-access-token': localStorage.getItem('token')
      },
      body: {
        URL: this.state.website
      }
    })
    const data = await response.json()
  }
  async componentDidMount() {
    let token = localStorage.getItem("token");
    if (token) {
      const user = jwt_decode(token);
      console.log(user);
      if (user) {
        this.setState({ name: user.name, loggedIn: true });
      }
    }
  }
  render() {
    return (
      <>
        <div className="main">
          <nav>
            <div className="link-1">
              <Link to="/register">Register</Link>
            </div>
            {this.state.loggedIn ? (
              <div
                className="link-2"
                onClick={() => {
                  localStorage.removeItem("token");
                  this.setState({ name: null, loggedIn: false });
                }}
              >
                Log out
              </div>
            ) : (
              <div>
                <Link to="/login">Login</Link>
              </div>
            )}
          </nav>
          <div className="app">
            {this.state.loggedIn 
            ? <form onSubmit={this.addWebsite}><input type="text" onChange={this.handleWebsiteChange}/><input type="submit" value="Submit" /></form>
            : <p>Log in to continue</p>}
          </div>
        </div>
      </>
    );
  }
}

export default Home;
