import { useState } from "react";

function Register(){
    let[state,setState] = useState({ name: "", email: "", password: "", failed: "" })


    let validateSubmit = async () => {
      const response = await fetch('http://localhost:8080/api/register', {
          method: "POST",
          header: {
              'Content-Type' : 'application/json'
          },
          body: JSON.stringify({
              name: state.name,
            email: state.email,
            password: state.password
          })
      })
      const data = await response.json();
      if(data.status === 'OK') alert("Login Successful");
      else alert('Login Failed, try again');
    }
      return (
        <>
          <div>
              <form onSubmit={validateSubmit}>
                  <input type="text" placeholder="Name" onChange={(e) => this.setState({name: e.target.value})}/>
                  <br/>
                  <input type="email" placeholder="Email" onChange={(e) => this.setState({email: e.target.value})}/>
                  <br/>
                  <input type="password" placeholder="Password" onChange={(e) => this.setState({password: e.target.value})}/>
                  <br/>
                  <input type="submit" value="register"/>
              </form>
          </div>
        </>
      );
}

export default Register;
