import React, { Component } from "react";
import jwt_decode from "jwt-decode";
import "./home.css";
import { Link } from "react-router-dom";

class AddWebsiteForm extends Component {
  render() {
    return (
      <form onSubmit={this.props.addWebsite}>
        <input type="text" onChange={this.props.onWebsiteChange} />
        <input type="submit" value="Submit" />
      </form>
    );
  }
}

class WebsiteList extends Component {
  state = { websites: [] }
  componentDidMount() {
    this.props.websites().then(data => this.setState({websites:data.websites}))
  } 
  render() {
    return (
      <ul>
        {this.state.websites.map(website => <li>{website}</li>)}
      </ul>
    )
  }
}

class Home extends Component {
  state = { name: null, loggedIn: false, website: "" };
  handleWebsiteChange = (e) => {
    this.setState({ website: e.target.value });
    console.log(this.state.website);
  };
  addWebsite = async (e) => {
    e.preventDefault();
    console.log("website:", this.state.website);
    const response = await fetch("http://localhost:8080/api/add_website", {
      method: "POST",
      headers: {
        "x-access-token": localStorage.getItem("token"),
        "content-Type": "application/json",
      },
      body: JSON.stringify({
        websiteURL: this.state.website,
      }),
    });
    const data = await response.json();
    console.log(data);
    this.setState();
  };
  getWebsites = async () => {
    const token = localStorage.getItem("token")
    if(!token) return [];
    const response = await fetch("http://localhost:8080/api/websites", {
      method: "GET",
      headers: {
        "x-access-token": token,
        "content-Type": "application/json",
      },
    });
    const data = await response.json();
    console.log(data);
    return data;
  };
  async componentDidMount() {
    let token = localStorage.getItem("token");
    if (token) {
      const user = jwt_decode(token);
      if (user) {
        this.setState({ name: user.name, loggedIn: true });
      }
    }
  }
  render() {
    console.log("websites: ", this.getWebsites());
    return (
      <>
        <div className="main">
          <nav>
            <div className="link-1">
              <Link to="/register">Register</Link>
            </div>
            {this.state.loggedIn ? (
              <div
                className="link-2"
                onClick={() => {
                  localStorage.removeItem("token");
                  this.setState({ name: null, loggedIn: false });
                }}
              >
                Log out
              </div>
            ) : (
              <div>
                <Link to="/login">Login</Link>
              </div>
            )}
          </nav>
          <div className="app">
            {this.state.loggedIn ? <><AddWebsiteForm addWebsite={this.addWebsite} onWebsiteChange={this.handleWebsiteChange}/><WebsiteList websites={this.getWebsites}/></> : <p>Log in to continue</p>}
          </div>
        </div>
      </>
    );
  }
}


export default Home;
