import React, { Component } from "react";
import ProgressBar from "./progressBar";
import "./websiteInfo.css";

class WebsiteInfo extends Component {
  Website = null;
  token = null;
  constructor() {
    super();
    this.Website = localStorage.getItem('website');
    console.log("Website: ", this.Website);
    this.Website = JSON.parse(localStorage.getItem("website"));
    console.log("Website: ", this.Website);
    setTimeout(() => {
        console.log('waiting')
    }, 1000);
    if (!this.Website) {
      window.location.href = "/home";
    }
    this.token = localStorage.getItem("token");
  }
  render() {
    return (
      <div className="info-main">
        <nav className="info-nav">
          <div className="website-title">{this.Website?.websiteURL}</div>
          {this.token && (
            <button onClick={() => console.log("Clicked")}>
              Add to dashboard
            </button>
          )}
        </nav>
        <div className="info">
            <div className="information">
                Information
                <ul>
                  <li><strong>Total Down Time: </strong>{this.Website.totalDowntime/(1000)} seconds</li>
                  <li><strong>Last Check: </strong>{this.Website.lastCheck.toLocaleString("en-US")} seconds</li>
                  {/* <li><strong>Total Down Time: </strong>{this.Website.totalDowntime/(1000)} seconds</li>
                  <li><strong>Total Down Time: </strong>{this.Website.totalDowntime/(1000)} seconds</li>
                  <li><strong>Total Down Time: </strong>{this.Website.totalDowntime/(1000)} seconds</li> */}
                </ul>
            </div>
            <div style={{padding: '5em', color: 'white', height: '90vh'}}>
              <ProgressBar size={36} percent={this.Website.uptimePercent} backgroundColor="rgb(25,25,34)"/>
            </div>
        </div>
      </div>
    );
  }
}

export default WebsiteInfo;
