import React, { Component } from "react";
import jwt_decode from 'jwt-decode'

class Home extends Component {
  name = null;
  componentDidMount() {
      const token = localStorage.getItem('token');
      if(token) {
        const user = jwt_decode(token);
        this.name = user?.name;
      }
  }
  render() {
    return <>
        <p>Hello {this.name}</p>
    </>;
  }
}

export default Home;
