import React, { Component } from "react";
import { Navigate as Redirect, Route } from 'react-router-dom'
import './login.css';

class Login extends Component {
  state = { email: "", password: "", failed: false, loggedIn: false };

  onComponentDidMount() {
    localStorage.getItem
  }
  validateSubmit = async (event)=> {
    event.preventDefault();
    const response = await fetch('http://localhost:8080/api/login', {
        method: "POST",
        headers: {
            'Content-Type' : 'application/json'
        },
        body: JSON.stringify({
            email: this.state.email,
            password: this.state.password
        })
    })
    const data = await response.json();
    console.log(data);
    if(data.status === 'ok') {
        localStorage.setItem('token',data.token);
        this.setState({failed: false, loggedIn: true})
    }
    else {
        this.setState({failed: true})
    }
  }
  render() {
    return (
      <>
        {this.state.loggedIn ? <Redirect to="/register"/>:0}
        <div className="login-box">
            {this.state.failed && <div className="failed-dialogue">Invalid Credentials, please try again </div>}
            <form className="login-form" onSubmit={this.validateSubmit}>
                <input type="email" placeholder="Email" onChange={(e) => this.setState({email: e.target.value})}/>
                <br/>
                <input type="password" placeholder="Password" onChange={(e) => this.setState({password: e.target.value})}/>
                <br/>
                <input type="submit" value="Login"/>
            </form>
        </div>
      </>
    );
  }
}

export default Login;
