import { useState } from "react";

function Login() {
  let[state,setState] = useState({ email: "", password: "", failed: "" });


  validateSubmit = async ()=> {
    const response = await fetch('http://localhost:8080/api/login', {
        method: "POST",
        header: {
            'Content-Type' : 'application/json'
        },
        body: JSON.stringify({
            email: state.email,
            password: state.password
        })
    })
    const data = await response.json();
    if(data.status === 'OK') alert("Login Successful");
    else alert('Login Failed, try again');
  }
    return (
        <>
        <div>
            <form onSubmit={validateSubmit}>
                <input type="email" placeholder="Email" onChange={(e) => setState({email: e.target.value})}/>
                <br/>
                <input type="password" placeholder="Password" onChange={(e) => setState({password: e.target.value})}/>
                <br/>
                <input type="submit" value="register"/>
            </form>
        </div>
        </>
    );
}

export default Login;
